const mongoose = require('mongoose');

// 1. Định nghĩa Schema cho User
const userSchema = new mongoose.Schema({
    name: {
        type: String,
        required: true // Yêu cầu trường này phải có
    },
    email: {
        type: String,
        required: true,
        unique: true, // Đảm bảo email không trùng lặp
        lowercase: true // Chuyển email sang chữ thường khi lưu
    },
    // Bạn có thể thêm trường createdAt, updatedAt tự động nếu muốn:
    createdAt: {
        type: Date,
        default: Date.now
    }
});

// 2. Tạo Model từ Schema
const User = mongoose.model('User', userSchema);

// 3. Export Model để sử dụng trong server.js
module.exports = User;