import React, { useEffect, useState } from "react";
import api from "../api/axiosConfig";

export default function AdminLogs() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchLogs = async () => {
      try {
        // ‚úÖ G·ªçi th·ª≠ route /logs tr∆∞·ªõc (n·∫øu backend ƒë√£ th√™m)
        let res = await api.get("/logs");
        setLogs(res.data);
      } catch (err) {
        console.warn("‚ö†Ô∏è Kh√¥ng t√¨m th·∫•y /api/logs, fallback sang /users");

        // ‚úÖ N·∫øu /logs ch∆∞a c√≥, fallback sang /users
        try {
          const res = await api.get("/users");
          // Chuy·ªÉn danh s√°ch user th√†nh d·∫°ng "fake log"
          const fakeLogs = res.data.map((u) => ({
            _id: u._id,
            user: { name: u.name, email: u.email },
            action: `Ng∆∞·ªùi d√πng ${u.role === "admin" ? "qu·∫£n tr·ªã" : "th∆∞·ªùng"}`,
            timestamp: u.createdAt || new Date().toISOString(),
          }));
          setLogs(fakeLogs);
        } catch (subErr) {
          console.error("‚ùå L·ªói t·∫£i d·ªØ li·ªáu:", subErr);
          setError("Kh√¥ng th·ªÉ t·∫£i logs!");
        }
      } finally {
        setLoading(false);
      }
    };

    fetchLogs();
  }, []);

  if (loading) return <p>‚è≥ ƒêang t·∫£i d·ªØ li·ªáu...</p>;

  return (
    <div className="card">
      <h2>üßæ Nh·∫≠t k√Ω ho·∫°t ƒë·ªông ng∆∞·ªùi d√πng</h2>
      {error && <p style={{ color: "red" }}>‚ùå {error}</p>}

      {logs.length === 0 ? (
        <p>Ch∆∞a c√≥ d·ªØ li·ªáu log n√†o.</p>
      ) : (
        <table className="table">
          <thead>
            <tr>
              <th>STT</th>
              <th>T√™n ng∆∞·ªùi d√πng</th>
              <th>Email</th>
              <th>H√†nh ƒë·ªông</th>
              <th>Th·ªùi gian</th>
            </tr>
          </thead>
          <tbody>
            {logs.map((log, i) => (
              <tr key={log._id || i}>
                <td>{i + 1}</td>
                <td>{log.user?.name || "Kh√¥ng r√µ"}</td>
                <td>{log.user?.email || "N/A"}</td>
                <td>{log.action}</td>
                <td>
                  {new Date(log.timestamp).toLocaleString("vi-VN", {
                    hour12: false,
                  })}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
