import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { selectUser, setLogin } from "../store/authSlice";
import api, { API_ORIGIN } from "../api/axiosConfig";

const withBase = (url) =>
  url?.startsWith("http") ? url : url ? `${API_ORIGIN}${url}` : "";

export default function Profile() {
  const dispatch = useDispatch();
  const storeUser = useSelector(selectUser);

  const [user, setUser] = useState(storeUser);
  const [name, setName] = useState(storeUser?.name || "");
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState("");
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setUser(storeUser);
    setName(storeUser?.name || "");
  }, [storeUser]);

  useEffect(() => {
    if (!file) return setPreview("");
    const reader = new FileReader();
    reader.onload = () => setPreview(reader.result || "");
    reader.readAsDataURL(file);
  }, [file]);

  // üü¢ C·∫≠p nh·∫≠t t√™n
  const updateName = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMsg("");
    try {
      const res = await api.put("users/profile", { name }); // b·ªè d·∫•u / ƒë·∫ßu
      const newUser = { ...user, name: res.data.name };
      const accessToken = localStorage.getItem("accessToken");
      const refreshToken = localStorage.getItem("refreshToken");
      dispatch(setLogin({ user: newUser, accessToken, refreshToken }));
      localStorage.setItem("user", JSON.stringify(newUser));
      setUser(newUser);
      setMsg("‚úÖ C·∫≠p nh·∫≠t t√™n th√†nh c√¥ng!");
      window.dispatchEvent(new Event("storage")); // ‚öôÔ∏è gi√∫p App.js sync l·∫°i user
    } catch (err) {
      console.error("L·ªói c·∫≠p nh·∫≠t:", err);
      setMsg("‚ùå Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t t√™n!");
    } finally {
      setLoading(false);
    }
  };

  // üü¢ Upload avatar
  const uploadAvatar = async () => {
    if (!file) return setMsg("‚ö†Ô∏è H√£y ch·ªçn ·∫£nh tr∆∞·ªõc!");
    setLoading(true);
    setMsg("");
    try {
      const form = new FormData();
      form.append("avatar", file);
      const res = await api.post("users/upload-avatar", form, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      const newUser = { ...user, avatar: res.data.avatarUrl };
      const accessToken = localStorage.getItem("accessToken");
      const refreshToken = localStorage.getItem("refreshToken");
      dispatch(setLogin({ user: newUser, accessToken, refreshToken }));
      localStorage.setItem("user", JSON.stringify(newUser));
      setUser(newUser);
      setMsg("‚úÖ Upload avatar th√†nh c√¥ng!");
      setPreview("");
      setFile(null);
      window.dispatchEvent(new Event("storage")); // ‚öôÔ∏è gi·ªØ avatar sau refresh
    } catch (err) {
      console.error("L·ªói upload:", err.response?.data || err.message);
      if (err.response?.status === 401)
        setMsg("‚ùå Token h·∫øt h·∫°n. Vui l√≤ng ƒëƒÉng nh·∫≠p l·∫°i.");
      else setMsg("‚ùå Upload th·∫•t b·∫°i!");
    } finally {
      setLoading(false);
    }
  };

  if (!user) return <p>ƒêang t·∫£i...</p>;

  const avatarSrc =
    preview ||
    withBase(user.avatar) ||
    "https://cdn-icons-png.flaticon.com/512/149/149071.png";

  const roleNote = {
    admin: "üåü B·∫°n l√† Admin ‚Äî to√†n quy·ªÅn qu·∫£n l√Ω ng∆∞·ªùi d√πng.",
    moderator: "üîß B·∫°n l√† Moderator ‚Äî c√≥ quy·ªÅn xem danh s√°ch user.",
    user: "üë§ B·∫°n l√† User ‚Äî ch·ªâ c√≥ th·ªÉ ch·ªânh s·ª≠a th√¥ng tin c√° nh√¢n.",
  };

  return (
    <>
      <h2>Th√¥ng tin c√° nh√¢n</h2>
      <div style={{ textAlign: "center", marginBottom: 16 }}>
        <img
          src={avatarSrc}
          alt="Avatar"
          style={{
            width: 120,
            height: 120,
            borderRadius: "50%",
            objectFit: "cover",
            border: "2px solid var(--accent)",
          }}
        />
        <p><b>T√™n:</b> {user.name}</p>
        <p><b>Email:</b> {user.email}</p>
        <p><b>Vai tr√≤:</b> {user.role}</p>
        <p style={{ color: "#fdd835" }}>{roleNote[user.role]}</p>
      </div>

      <form onSubmit={updateName} className="form">
        <h3>C·∫≠p nh·∫≠t t√™n</h3>
        <input value={name} onChange={(e) => setName(e.target.value)} required />
        <button className="btn" disabled={loading}>
          {loading ? "ƒêang l∆∞u..." : "L∆∞u thay ƒë·ªïi"}
        </button>
      </form>

      <hr style={{ margin: "16px 0" }} />

      <div style={{ textAlign: "center" }}>
        <h3>Upload Avatar</h3>
        <input
          type="file"
          accept="image/*"
          onChange={(e) => setFile(e.target.files?.[0] || null)}
        />
        <button
          className="btn"
          style={{ marginTop: 10 }}
          onClick={uploadAvatar}
          disabled={loading || !file}
        >
          {loading ? "ƒêang t·∫£i..." : "T·∫£i ·∫£nh l√™n"}
        </button>
      </div>

      {msg && <p style={{ textAlign: "center", color: "var(--accent)" }}>{msg}</p>}
    </>
  );
}
