import React, { useEffect, useState, useCallback } from "react";
import { useSelector, useDispatch } from "react-redux";
import { selectUser, setLogin } from "../store/authSlice";
import api, { API_ORIGIN } from "../api/axiosConfig";
import Cropper from "react-easy-crop";
import getCroppedImg from "../utils/cropImage"; // helper mới thêm ở dưới

export default function Profile() {
  const dispatch = useDispatch();
  const storeUser = useSelector(selectUser);
  const [user, setUser] = useState(storeUser);
  const [name, setName] = useState(storeUser?.name || "");
  const [file, setFile] = useState(null);
  const [preview, setPreview] = useState("");
  const [msg, setMsg] = useState("");
  const [loading, setLoading] = useState(false);

  // cropper state
  const [crop, setCrop] = useState({ x: 0, y: 0 });
  const [zoom, setZoom] = useState(1);
  const [croppedAreaPixels, setCroppedAreaPixels] = useState(null);
  const [showCropper, setShowCropper] = useState(false);

  useEffect(() => {
    setUser(storeUser);
    setName(storeUser?.name || "");
  }, [storeUser]);

  const onFileChange = (e) => {
    const selected = e.target.files?.[0];
    if (!selected) return;
    setFile(selected);
    const reader = new FileReader();
    reader.onload = () => setPreview(reader.result || "");
    reader.readAsDataURL(selected);
    setShowCropper(true);
  };

  const onCropComplete = useCallback((_, croppedPixels) => {
    setCroppedAreaPixels(croppedPixels);
  }, []);

  const uploadAvatar = async () => {
    if (!file || !croppedAreaPixels) return setMsg("⚠️ Hãy chọn và crop ảnh!");

    try {
      setLoading(true);
      const croppedBlob = await getCroppedImg(preview, croppedAreaPixels);
      const form = new FormData();
      form.append("avatar", croppedBlob, "avatar.jpg");

      const res = await api.post("/users/upload-avatar", form, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      const newUser = { ...user, avatar: res.data.avatarUrl };
      const accessToken = localStorage.getItem("accessToken");
      const refreshToken = localStorage.getItem("refreshToken");
      dispatch(setLogin({ user: newUser, accessToken, refreshToken }));
      localStorage.setItem("user", JSON.stringify(newUser));
      setUser(newUser);
      setMsg("✅ Upload avatar thành công!");
      setShowCropper(false);
      setPreview("");
      setFile(null);
    } catch (err) {
      console.error("Lỗi upload:", err);
      setMsg("❌ Upload thất bại!");
    } finally {
      setLoading(false);
    }
  };

  const avatarSrc =
    (user?.avatar?.startsWith("http") ? user.avatar : `${API_ORIGIN}${user?.avatar}`) ||
    "https://cdn-icons-png.flaticon.com/512/149/149071.png";

  return (
    <>
      <h2>Thông tin cá nhân</h2>
      <div style={{ textAlign: "center", marginBottom: 16 }}>
        <img
          src={avatarSrc}
          alt="Avatar"
          style={{
            width: 120,
            height: 120,
            borderRadius: "50%",
            objectFit: "cover",
            border: "2px solid var(--accent)",
          }}
        />
        <p><b>Tên:</b> {user.name}</p>
        <p><b>Email:</b> {user.email}</p>
      </div>

      <div style={{ textAlign: "center" }}>
        <h3>Upload Avatar</h3>
        <input type="file" accept="image/*" onChange={onFileChange} />
      </div>

      {showCropper && (
        <div style={{ position: "relative", width: "100%", height: 300, marginTop: 16 }}>
          <Cropper
            image={preview}
            crop={crop}
            zoom={zoom}
            aspect={1}
            cropShape="round"
            showGrid={false}
            onCropChange={setCrop}
            onZoomChange={setZoom}
            onCropComplete={onCropComplete}
          />
          <input
            type="range"
            min={1}
            max={3}
            step={0.1}
            value={zoom}
            onChange={(e) => setZoom(e.target.value)}
            style={{ width: "100%", marginTop: 10 }}
          />
          <button className="btn" onClick={uploadAvatar} disabled={loading}>
            {loading ? "Đang tải..." : "Cắt và tải ảnh lên"}
          </button>
        </div>
      )}

      {msg && <p style={{ textAlign: "center", color: "var(--accent)" }}>{msg}</p>}
    </>
  );
}
