import React, { useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
  Link,
  useNavigate,
} from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import {
  selectIsAuthenticated,
  selectUser,
  setLogout,
  setLogin,
} from "./store/authSlice";
import api from "./api/axiosConfig";

import Register from "./pages/Register";
import Login from "./pages/Login";
import Profile from "./pages/Profile";
import AdminUserList from "./pages/AdminUserList";

// üü¢ Th√™m 2 d√≤ng m·ªõi
import ForgotPassword from "./pages/ForgotPassword";
import ResetPassword from "./pages/ResetPassword";

import "./App.css";

const safeParse = (text) => {
  try {
    if (!text || typeof text !== "string") return null;
    return JSON.parse(text);
  } catch {
    return null;
  }
};

function App() {
  const dispatch = useDispatch();
  const isAuthenticated = useSelector(selectIsAuthenticated);
  const currentUser = useSelector(selectUser);

  // üü¢ Bootstrap Redux state t·ª´ localStorage
  const loadUserFromLocal = () => {
    const savedUser = safeParse(localStorage.getItem("user"));
    const accessToken = localStorage.getItem("accessToken");
    const refreshToken = localStorage.getItem("refreshToken");
    if (savedUser && accessToken) {
      dispatch(setLogin({ user: savedUser, accessToken, refreshToken: refreshToken || null }));
    }
  };

  useEffect(() => {
    loadUserFromLocal();
    // üü¢ Nghe event storage ƒë·ªÉ sync l·∫°i avatar sau khi upload
    window.addEventListener("storage", loadUserFromLocal);
    return () => window.removeEventListener("storage", loadUserFromLocal);
  }, [dispatch]);

  // üîπ N√∫t ƒêƒÉng xu·∫•t
  const LogoutButton = () => {
    const navigate = useNavigate();
    const handleLogout = async () => {
      try {
        await api.post("/auth/logout", {
          refreshToken: localStorage.getItem("refreshToken"),
        });
      } catch (err) {
        console.warn("API logout b√°o l·ªói (b·ªè qua):", err?.response?.data || err.message);
      } finally {
        dispatch(setLogout());
        navigate("/login");
      }
    };
    return (
      <button
        onClick={handleLogout}
        className="btn btn-sm"
        style={{ background: "var(--danger)" }}
      >
        ƒêƒÉng xu·∫•t
      </button>
    );
  };

  // üîê Route b·∫£o v·ªá
  const PrivateRoute = ({ children }) =>
    isAuthenticated ? children : <Navigate to="/login" replace />;

  // üéØ AdminRoute cho c·∫£ admin v√† moderator
  const AdminRoute = ({ children }) => {
    if (!isAuthenticated) return <Navigate to="/login" replace />;
    if (!currentUser) return <div className="card">ƒêang t·∫£i th√¥ng tin...</div>;
    return currentUser.role === "admin" || currentUser.role === "moderator"
      ? children
      : <Navigate to="/" replace />;
  };

  return (
    <Router>
      <div className="container">
        <div className="header">
          <h1>Qu·∫£n L√Ω User</h1>
          <nav style={{ marginBottom: "20px" }}>
            {isAuthenticated ? (
              <>
                <Link to="/" style={{ marginRight: "15px", color: "var(--text)" }}>
                  Profile
                </Link>
                {(currentUser?.role === "admin" || currentUser?.role === "moderator") && (
                  <Link to="/admin/users" style={{ marginRight: "15px", color: "orange" }}>
                    Admin Users
                  </Link>
                )}
                <LogoutButton />
              </>
            ) : (
              <>
                <Link to="/login" style={{ marginRight: "15px", color: "var(--text)" }}>
                  ƒêƒÉng nh·∫≠p
                </Link>
                <Link to="/register" style={{ marginRight: "15px", color: "var(--text)" }}>
                  ƒêƒÉng k√Ω
                </Link>
                {/* üü¢ Th√™m link ‚ÄúQu√™n m·∫≠t kh·∫©u‚Äù */}
                <Link to="/forgot-password" style={{ color: "var(--accent)" }}>
                  Qu√™n m·∫≠t kh·∫©u
                </Link>
              </>
            )}
          </nav>
        </div>

        {/* ROUTES */}
        <Routes>
          {/* Public */}
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* üü¢ Th√™m 2 route m·ªõi */}
          <Route path="/forgot-password" element={<ForgotPassword />} />
          <Route path="/reset-password/:resetToken" element={<ResetPassword />} />

          {/* Private */}
          <Route
            path="/"
            element={
              <PrivateRoute>
                <div className="card">
                  <Profile />
                </div>
              </PrivateRoute>
            }
          />

          {/* Admin & Moderator */}
          <Route
            path="/admin/users"
            element={
              <AdminRoute>
                <div className="card">
                  <AdminUserList currentUser={currentUser} />
                </div>
              </AdminRoute>
            }
          />

          {/* Fallback */}
          <Route
            path="*"
            element={<Navigate to={isAuthenticated ? "/" : "/login"} replace />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
