import React, { useEffect, useState } from "react";
import api from "../api/axiosConfig";

export default function AdminLogs() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchLogs = async () => {
      try {
        // ‚úÖ G·ªçi th·ª≠ /logs
        const res = await api.get("/logs");
        setLogs(res.data);
      } catch (err) {
        console.warn("‚ö†Ô∏è Kh√¥ng t√¨m th·∫•y /api/logs, fallback sang /users");
        try {
          const res = await api.get("/users");
          const fakeLogs = res.data.map((u) => ({
            _id: u._id,
            user: { name: u.name, email: u.email },
            action: `Ng∆∞·ªùi d√πng ${u.role === "admin" ? "qu·∫£n tr·ªã" : u.role}`,
            timestamp: u.createdAt || new Date().toISOString(),
          }));
          setLogs(fakeLogs);
        } catch (subErr) {
          console.error("‚ùå L·ªói t·∫£i d·ªØ li·ªáu:", subErr);
          setError("Kh√¥ng th·ªÉ t·∫£i logs!");
        }
      } finally {
        setLoading(false);
      }
    };
    fetchLogs();
  }, []);

  if (loading)
    return (
      <div className="card" style={{ textAlign: "center", padding: "40px" }}>
        <p style={{ fontSize: "18px", color: "var(--accent)" }}>
          ‚è≥ ƒêang t·∫£i d·ªØ li·ªáu...
        </p>
      </div>
    );

  return (
    <div
      className="card"
      style={{
        background: "var(--card)",
        padding: "30px",
        borderRadius: "18px",
        boxShadow: "0 4px 20px rgba(0,0,0,0.3)",
      }}
    >
      <h2
        style={{
          marginBottom: "20px",
          color: "var(--accent)",
          display: "flex",
          alignItems: "center",
          gap: "10px",
        }}
      >
        üßæ Nh·∫≠t k√Ω ho·∫°t ƒë·ªông ng∆∞·ªùi d√πng
      </h2>

      {error && (
        <p
          style={{
            color: "var(--danger)",
            background: "#3a1d1d",
            padding: "10px",
            borderRadius: "8px",
            textAlign: "center",
            fontWeight: "bold",
          }}
        >
          ‚ùå {error}
        </p>
      )}

      {logs.length === 0 ? (
        <p style={{ textAlign: "center", color: "#aaa" }}>
          Ch∆∞a c√≥ d·ªØ li·ªáu log n√†o.
        </p>
      ) : (
        <div
          style={{
            overflowX: "auto",
            borderRadius: "10px",
            background: "#1b1f2a",
          }}
        >
          <table
            className="table"
            style={{
              width: "100%",
              borderCollapse: "collapse",
              color: "#ddd",
            }}
          >
            <thead>
              <tr style={{ background: "#2b3242" }}>
                <th style={thStyle}>#</th>
                <th style={thStyle}>üë§ Ng∆∞·ªùi d√πng</th>
                <th style={thStyle}>üìß Email</th>
                <th style={thStyle}>‚öôÔ∏è H√†nh ƒë·ªông</th>
                <th style={thStyle}>üïí Th·ªùi gian</th>
              </tr>
            </thead>
            <tbody>
              {logs.map((log, i) => (
                <tr
                  key={log._id || i}
                  style={{
                    background: i % 2 === 0 ? "#222836" : "#1e2330",
                    transition: "all 0.2s",
                  }}
                  onMouseEnter={(e) =>
                    (e.currentTarget.style.background = "#2c3345")
                  }
                  onMouseLeave={(e) =>
                    (e.currentTarget.style.background =
                      i % 2 === 0 ? "#222836" : "#1e2330")
                  }
                >
                  <td style={tdStyle}>{i + 1}</td>
                  <td style={tdStyle}>{log.user?.name || "Kh√¥ng r√µ"}</td>
                  <td style={tdStyle}>{log.user?.email || "N/A"}</td>
                  <td style={{ ...tdStyle, color: "var(--accent)" }}>
                    {log.action}
                  </td>
                  <td style={tdStyle}>
                    {new Date(log.timestamp).toLocaleString("vi-VN", {
                      hour12: false,
                    })}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

// üé® Style cell t√°i s·ª≠ d·ª•ng
const thStyle = {
  padding: "12px 10px",
  textAlign: "left",
  color: "#ffda6b",
  fontWeight: "bold",
  fontSize: "15px",
};

const tdStyle = {
  padding: "10px",
  borderBottom: "1px solid #2f3749",
  fontSize: "14px",
};
