import React, { useEffect } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
  Link,
  useNavigate,
} from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import {
  selectIsAuthenticated,
  selectUser,
  setLogout,
  setLogin,
} from "./store/authSlice";
import api from "./api/axiosConfig";

import Register from "./pages/Register";
import Login from "./pages/Login";
import Profile from "./pages/Profile";
import AdminUserList from "./pages/AdminUserList";
import ForgotPassword from "./pages/ForgotPassword";
import ResetPassword from "./pages/ResetPassword";
import AdminLogs from "./pages/AdminLogs"; // üÜï Trang hi·ªÉn th·ªã ho·∫°t ƒë·ªông ng∆∞·ªùi d√πng
import "./App.css";

const safeParse = (text) => {
  try {
    if (!text || typeof text !== "string") return null;
    return JSON.parse(text);
  } catch {
    return null;
  }
};

function App() {
  const dispatch = useDispatch();
  const isAuthenticated = useSelector(selectIsAuthenticated);
  const currentUser = useSelector(selectUser);

  // üß© Load d·ªØ li·ªáu t·ª´ localStorage khi reload trang
  const loadUserFromLocal = () => {
    const savedUser = safeParse(localStorage.getItem("user"));
    const accessToken = localStorage.getItem("accessToken");
    const refreshToken = localStorage.getItem("refreshToken");
    if (savedUser && accessToken) {
      dispatch(setLogin({ user: savedUser, accessToken, refreshToken: refreshToken || null }));
    }
  };

  useEffect(() => {
    loadUserFromLocal();
    window.addEventListener("storage", loadUserFromLocal);
    return () => window.removeEventListener("storage", loadUserFromLocal);
  }, [dispatch]);

  // üîπ ƒêƒÉng xu·∫•t
  const LogoutButton = () => {
    const navigate = useNavigate();
    const handleLogout = async () => {
      try {
        await api.post("/auth/logout", {
          refreshToken: localStorage.getItem("refreshToken"),
        });
      } catch (err) {
        console.warn("API logout l·ªói:", err?.response?.data || err.message);
      } finally {
        dispatch(setLogout());
        navigate("/login");
      }
    };
    return (
      <button
        onClick={handleLogout}
        className="btn btn-sm"
        style={{ background: "var(--danger)", marginLeft: 10 }}
      >
        ƒêƒÉng xu·∫•t
      </button>
    );
  };

  // üîê Route ch·ªâ cho ng∆∞·ªùi ƒë√£ ƒëƒÉng nh·∫≠p
  const PrivateRoute = ({ children }) =>
    isAuthenticated ? children : <Navigate to="/login" replace />;

  // üéØ Route cho admin/moderator
  const AdminRoute = ({ children }) => {
    if (!isAuthenticated) return <Navigate to="/login" replace />;
    if (!currentUser) return <div className="card">ƒêang t·∫£i th√¥ng tin...</div>;
    return currentUser.role === "admin" || currentUser.role === "moderator"
      ? children
      : <Navigate to="/" replace />;
  };

  return (
    <Router>
      <div className="container">
        {/* üß≠ HEADER */}
        <div className="header">
          <h1>üß© Qu·∫£n L√Ω Ng∆∞·ªùi D√πng</h1>
          <nav style={{ marginBottom: "20px" }}>
            {isAuthenticated ? (
              <>
                <Link to="/" style={{ marginRight: 15, color: "var(--text)" }}>
                  Profile
                </Link>

                {(currentUser?.role === "admin" || currentUser?.role === "moderator") && (
                  <>
                    <Link
                      to="/admin/users"
                      style={{ marginRight: 15, color: "orange" }}
                    >
                      Admin Users
                    </Link>
                    <Link
                      to="/admin/logs"
                      style={{ marginRight: 15, color: "#26a69a" }}
                    >
                      Logs
                    </Link>
                  </>
                )}

                <LogoutButton />
              </>
            ) : (
              <>
                <Link
                  to="/login"
                  style={{ marginRight: 15, color: "var(--text)" }}
                >
                  ƒêƒÉng nh·∫≠p
                </Link>
                <Link
                  to="/register"
                  style={{ marginRight: 15, color: "var(--text)" }}
                >
                  ƒêƒÉng k√Ω
                </Link>
                <Link to="/forgot-password" style={{ color: "var(--accent)" }}>
                  Qu√™n m·∫≠t kh·∫©u
                </Link>
              </>
            )}
          </nav>
        </div>

        {/* üß≠ ROUTES */}
        <Routes>
          {/* Public routes */}
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/forgot-password" element={<ForgotPassword />} />
          <Route path="/reset-password/:resetToken" element={<ResetPassword />} />

          {/* Private routes */}
          <Route
            path="/"
            element={
              <PrivateRoute>
                <div className="card">
                  <Profile />
                </div>
              </PrivateRoute>
            }
          />

          {/* Admin & Moderator */}
          <Route
            path="/admin/users"
            element={
              <AdminRoute>
                <div className="card">
                  <AdminUserList currentUser={currentUser} />
                </div>
              </AdminRoute>
            }
          />

          {/* Logs */}
          <Route
            path="/admin/logs"
            element={
              <AdminRoute>
                <div className="card">
                  <AdminLogs />
                </div>
              </AdminRoute>
            }
          />

          {/* Fallback */}
          <Route
            path="*"
            element={<Navigate to={isAuthenticated ? "/" : "/login"} replace />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
